// Pega o texto concatenado vindo do Join Ideas
const historico = $json.mergedText || "";

// Separa em ideias individuais (aqui o separador Ã© vÃ­rgula + espaÃ§o)
const ideias = historico.split(",").map(i => i.trim()).filter(i => i);

// Extrai o "tema" de cada ideia (tudo atÃ© o primeiro ":")
const temas = ideias.map(ideia => {
  const match = ideia.match(/^([^:]+):/); 
  return match ? match[1].trim() : null;
}).filter(Boolean);

// Conta a frequÃªncia de cada tema
const contagem = temas.reduce((acc, tema) => {
  acc[tema] = (acc[tema] || 0) + 1;
  return acc;
}, {});

// Descobre o tema menos usado
